import React,{useState} from 'react'
import './login.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Header from '../../Components/Header/Header';

const Login = () => {

    const [username,setUsername] = useState("")
    const [password,setPassword] = useState("")
    const loginHandler = (event) => {

     
        axios.post('http://127.0.0.1:8000/api/accounts/token',{
        username:username,
        password:password,
        }).then( res => {
            if(res.data)
            {
                const token = res.data.token;
                axios.get('http://127.0.0.1:8000/api/accounts/login',
               { 
                   Header:{
                    Authorization: 'token ' +token,
                }
            }).then( res => {

                console.log(res.data);
            })


            }
        })


    }
    return (
        <div className="login">
            <span className="loginTitle">Login</span>
            <form action="" className="loginForm">
                <label>
                    Username
                </label>
                <input type="text" required value={username} onChange={ (e) => { setUsername(e.target.value)}} placeholder="enter your username..." />
                <label>
                    Password
                </label>
                <input type="password" required value={password} onChange={ (e) => { setPassword(e.target.value)}} placeholder="enter password" />
                <button className="loginButton"> <Link className="link" onClick={loginHandler} to="/login">Login</Link></button>
                <button className="loginRegisterButton"> <Link className="link" to="/register">Register</Link></button>
               

            </form>
        </div>
    )
}

export default Login;
