import axios from 'axios';
import React,{useState,useContext} from 'react'
import { useHistory } from 'react-router';
import { UserContext } from '../../Context/User/UserContext';
import { PostsContext } from '../../Context/PostsContext/PostsContext';
import './Write.css';

const Write = () => {

    const postURL = 'http://127.0.0.1:8000/api/posts/create/'
    
    const [user,setUser] = useContext(UserContext);
    const [posts,setPosts] = useContext(PostsContext);


    const [title,setTitle ]  = useState('');
    const [msg,setMsg ]  = useState('');
    const token = user? user.token : '';
    
    const history = useHistory();
    const postHandler = (e) => {


        if(user===undefined)
        {
            history.push("/login");
            return;
        }
        console.log(token);
        e.preventDefault();
        axios.post(postURL,
            {
                title: title,
               message: msg,
            },
            {
                headers: {
                    Authorization: 'token 787fee64caed085498829a37a84986778385f158',
                   },
        }).then(
            res => {
                console.log(res);


            }
        ).catch ( err => {
            console.log(err);
        })
    }


    return (
        <div className="write">
            <form className="writeForm">
            </form>
                <div className="writeFormGroup">
                    <input type="text" placeholder="Title" value={title} onChange={ (e) => setTitle(e.target.value)} className="writeInput" autoFocus={true} />
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder="Write post..." type="text" value={msg} onChange={ (e) => setMsg(e.target.value)} className="writeInput writeText">
                    </textarea>
                </div>

                <div className="writeFormGroup">
                <button type="submit" className="writeSubmit" onClick={postHandler}>Post</button>

                </div>
        </div>
    )
}

export default Write
