import React,{useContext} from 'react'
import { Link,useHistory } from 'react-router-dom';
import { UserContext } from '../../Context/User/UserContext';
import { GroupContext } from '../../Context/GroupContext/GroupContext';
import './Group.css';

import axios from 'axios';

// return <Group key={g.id} name={g.name} desc={g.description} members={g.members.count} posts={g.posts.length} />
const groupLeave = (slug) => `http://127.0.0.1:8000/api/groups/leave/${slug}`;
const groupJoin = (slug) => `http://127.0.0.1:8000/api/groups/join/${slug}`;
const allGroupsURL = 'http://127.0.0.1:8000/api/groups/alljoined';



const Group = (props) => {

    const [user] = useContext(UserContext);
    const [groups,setGroups] = useContext(UserContext);
    const token = user ? user.token : '';
    const history = useHistory();


    const refreshContent = (token)=> {

        if(token=='' || token==undefined)
        {
            history.push("/login");
            return;
        }
        axios.get(allGroupsURL,{
            headers : {
                Authorization: 'token '+token,
            }
        }).then(
            res => {
                setGroups(prev => prev = res.data);
            }
        )
    }
   

   
    const exitHandlder = (slug) => {
        const url = groupLeave(slug);
        axios.get(url,{
            headers : {
                Authorization : 'token '+token,
            }
        });

        // refreshContent(token,setGroups);
    }

    const joinHandler = (slug) => {
       
        const url = groupJoin(slug);
        axios.get(url,{
            headers : {
                Authorization : 'token '+token,
            }
        });

        // refreshContent(token,setGroups);
    }
    
    return (
        <div>
            <div className="group">
                <div className="groupHead">
                    <span className="groupTitle">
                        <Link className="link" to={`/group/${props.id}`} > {props.name} </Link>
                    </span>
                 { props.isJoined ? <span className="groupExit"> <i onClick={() => exitHandlder(props.slug) } class="fas fa-sign-out-alt"></i> </span>
                    : <span className="groupJoin"> <i onClick={() => joinHandler(props.slug) } class="fas fa-user-plus"></i> </span>}
                </div>
                <hr />
                <div className="groupMeta">
                    <span className="groupdate"> members: {props.members}</span>
                    <span className="groupdate">  posts:{props.posts}</span>
                </div>

            <p className="groupdesc">
                {props.desc}
            </p>

            </div>
        </div>
    )
}

export default Group
