import React,{useContext} from 'react'
import { Link } from 'react-router-dom';
import { UserContext } from '../../Context/User/UserContext';
import { GroupContext } from '../../Context/GroupContext/GroupContext';
import './Group.css';

import axios from 'axios';


const groupLeave = (slug) => `http://127.0.0.1:8000/api/groups/leave/${slug}`;
const groupJoin = (slug) => `http://127.0.0.1:8000/api/groups/join/${slug}`;
const allGroupsURL = 'http://127.0.0.1:8000/api/groups/all';


const Group = (props) => {

   

    const [user] = useContext(UserContext);
    const [groups,setGroups] = useContext(UserContext);
    const token = user ? user.token : '';
    const exitHandlder = (slug) => {
        
        axios.get(url,{
            headers : {
                Authorization : 'token '+token,
            }
        });

        axios.get(allGroupsURL,{
            headers : {
                Authorization: 'token '+token,
            }
        }).then(
            res => {
                setGroups(res.data);
            }
        )


    }
    
    return (
        <div>
            <div className="group">
                <div className="groupHead">
                    <span className="groupTitle">
                        <Link className="link" to={`/group/${props.id}`} > {props.name} </Link>
                    </span>
                 { props.isJoined ? <span className="groupExit"> <i onClick={() => exitHandlder(props.slug) } class="fas fa-sign-out-alt"></i> </span>
                    : <span className="groupJoin"> <i class="fas fa-user-plus"></i> </span>}
                </div>
                <hr />
                <div className="groupMeta">
                    <span className="groupdate"> members: {props.members}</span>
                    <span className="groupdate">  posts:{props.posts}</span>
                </div>

            <p className="groupdesc">
                {props.desc}
            </p>

            </div>
        </div>
    )
}

export default Group
