const allPostURL = 'http://127.0.0.1:8000/api/posts/all/';

const Posts = () => {


   
    // const [posts,setPosts] = useContext(PostsContext);


    
    return (
        <div className="posts">

            <PostsContext.Consumer>
             { posts => { console.log(posts[0]); posts[0].map( p => {
                     <Post key={p.id} title={p.title} message={p.message} updated_at={p.updated_at}  username={p.username} />
                })} 
               }
            </PostsContext.Consumer>
           
        </div>
    )
}

export default Posts
