import React,{useContext,useState} from 'react'
import './Sidebar.css';
import { useHistory, } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../../Context/User/UserContext';
import { GroupContext } from '../../Context/GroupContext/GroupContext';
import Group from '../Group/Group';

const allGroupsURL = 'http://127.0.0.1:8000/api/groups/all'
const addGroupURL = 'http://127.0.0.1:8000/api/groups/new/';

const Sidebar = () => {

    const history = useHistory();

    const name= useState('');
    const desc = useState('');
    const [clickCreate,setclickCreate] = useState(false) ;

    const loadGroups = (token) => {
        if(token=='' || token==undefined)
        {
            history.push('/login');
            return;
        }
    
        axios.get(allGroupsURL,{
            headers : {
                Authorization : 'token '+token,
            }
        }).then( res => {
            setGroups( prev => prev = res.data);
        })

    }


    const addGroupHandler = (token) => {

        // axios.post(addGroupURL,{
        //     name : name,
        //     description : desc,
        // },{
        //     headers : {
        //         Authorization : 'token ' + token,
        //     }
        // })
    }


    const [user] = useContext(UserContext);
    
    const [groups,setGroups] = useContext(GroupContext);
    
    const token = user ? user.token : '';
    

    return (
        <div className="sidebar">
            {clickCreate ? <div>
                <form className="createGroup">
                    <div className="createGroupForm">
                    <input type="text" className="groupInput" placeholder="Enter Group Name..." />
                    <textarea type="text" className="writeInput"  placeholder="Enter Group Description..."/>
                    <button type="submit" className="writeSubmit" onClick={() => addGroupHandler(token)}>create</button>
                    </div>
                </form>
            </div> : ""}
           <div className="groupControls">
            <i onClick={ () => setclickCreate(!clickCreate)} className="addGroup fas fa-plus-circle"  ></i>
            <i   onClick={ () => loadGroups(token) } className="reload fas fa-redo"></i>
           </div>
            
            { groups.map( g => {
               return <Group key={g.id} name={g.name} reload={(token) => loadGroups(token)} desc={g.description} members={g.members.length} posts={g.posts.length} slug={g.slug} isJoined={g.members.includes(user.id)} />
            })}
            

        </div>
    )
}

export default Sidebar
