import React,{useState,useContext,createContext} from "react";
import axios from "axios";
import { UserContext } from '../../Context/User/UserContext';


const allPostURL = 'http://127.0.0.1:8000/api/posts/all/';

export const PostsContext = createContext();

export const PostsProivder = props => {
    const [posts,setPosts] = useState([]);
    const [user,setUser] = useContext(UserContext);
    const token = user ? user.token:"";
    //getInitialPost(setPosts,token);

    return <PostsContext.Provider value={[posts,setPosts]}>
        {props.children}
    </PostsContext.Provider>
}

const getInitialPost = (sp,token) => {

    
    axios.get(allPostURL,{
                headers : {
                    Authorization: 'token '+token,
                }
            }).then(
                res => {
                    sp(res.data);
                }
            )
}
