import React,{useContext} from 'react';
import { PostsContext } from '../../Context/PostsContext/PostsContext';
import './SinglePost.css';
import { Link } from 'react-router-dom';

const getPost = (posts,id) => {

    posts.forEach(element => {
        console.log(element);
    });
}


const SinglePost = (props) => {


   
    const id = props.id 
    const [posts,setPosts] = useContext(PostsContext);
    const post = posts.filter(
        p => {
            return p.id == id;
        })[0];

    console.log('sp= ',post);


    return (
        post ? <div className="singlePost">
            <div className="singlePostWrapper">
                <h1 className="singlePostTitle"> {post.title}
                    <div className="singlePostEdit">
                        <i className="singlePostIcon far fa-edit"></i>
                        <Link to="/delete" className="link"> <i className="singlePostIcon fas fa-trash-alt"></i> </Link>
                        
                    </div>
                </h1>
                <div className="singlePostInfo">
                    <span className="singlePostAuthor">Author: <b>{post.username}</b></span>
                    <span className="singlePostDate">Date: <b>{new Date(post.updated_at).toLocaleDateString()}</b></span>
                </div>
                <p className="singlePostDesc">
                    {post.message}
                </p>
            </div>
        </div> :  <h3 className="singlePost singlePostTitle" >No post selected</h3>
    )
}

export default SinglePost
