import React,{useContext} from 'react'
import './Sidebar.css';
import axios from 'axios';
import { UserContext } from '../../Context/User/UserContext';
import { GroupContext } from '../../Context/GroupContext/GroupContext';
import Group from '../Group/Group';

const allGroupsURL = 'http://127.0.0.1:8000/api/groups/all'

const Sidebar = () => {

    const loadGroups = () => {

        axios.get(allGroupsURL,{
            headers : {
                Authorization : 'token '+token,
            }
        }).then( res => {
            console.log(res.data);
        })

    }


    const [user] = useContext(UserContext);
    
    const [groups] = useContext(GroupContext);

    const token = user ? user.token : '';

    return (
        <div className="sidebar">
           <div className="groupControls">
            <i className="fas fa-plus-circle"></i>
            <i  onClick={loadGroups} className="reload fas fa-redo"></i>
           </div>
            
            { groups.map( g => {
               return <Group key={g.id} name={g.name} desc={g.description} members={g.members.length} posts={g.posts.length} />
            })}

        </div>
    )
}

export default Sidebar
