import React,{useState} from 'react'
import './register.css';
import { Link } from 'react-router-dom';
import axios from 'axios';



const registerURL = 'http://127.0.0.1:8000/api/accounts/register'




const Register = () => {

    const [username,setUsername] = useState('')
    const [password,setPassword] = useState('')
    const [password2,setPassword2] = useState('')
    const [email,setEmail] = useState('')


    const [user, setUser] = useState({
        username:'',email:'',password:'',token:'',message : '',
    });

    const registerUser = (e) => {
        
       e.preventDefault();
       if( password !==password2)
        alert("password do not match")
        
        axios.post(registerURL,{
            username:username,
            email : email,
            password:password,
            password2:password2,
            
        }).then(
            res => {
                console.log(res);
                if(res.data.response && res.data.response.includes("successfully"))
                {
                    setUser({
                        username:res.data.username,
                        email:res.data.email,
                        password:res.data.password,
                        token : res.data.token,
                        message : res.data.response,
                    })
                }
                else{
                    alert({...res.data});
                }
               
            }
        )

    //  console.log({username},{email},{password});
    //  console.log(username,email,password);
        
    }

    return (
        <div className="register">
            <span className="registerTitle">Register</span>
            <form action="" className="registerForm">
                <label>
                    Username
                </label>
                <input type="text" value={username} onChange= { (e)=> setUsername(e.target.value)} placeholder="Enter your username..." />
                <label>
                    Email
                </label>
                <input type="email" value={email} onChange= { (e)=> setEmail(e.target.value)} placeholder="Enter your email..." />
                <label>
                    Password
                </label>
                <input type="password" value={password} onChange= { (e)=> setPassword(e.target.value)} placeholder="Enter password" />
                <label>
                    Confirm Password
                </label>
                <input type="password" value={password2} onChange= { (e)=> setPassword2(e.target.value)}  placeholder="Confirm password" />
                <button className="registerButton" onClick={registerUser}> Register</button>
                <button className="registerLoginButton"> <Link className="link" to="/login">Login</Link></button>

            </form>
        </div>
    )
}

export default Register;
