import React, { useState,useContext,useEffect } from 'react'
import Post from '../Post/Post';
import './Posts.css';
import { PostsProivder,PostsContext } from '../../Context/PostsContext/PostsContext';

import axios from 'axios';

const Posts = () => {


   
    const [posts,setPosts] = useContext(PostsContext);
    const [allposts,setAllPosts] = useState(posts);

    useEffect( () => {

        console.log(allposts.length,posts.length);
        setPosts(posts); 
        
    });

    
    return (
        <div className="posts">
           { posts.map( p => {
               return <Post key={p.id} title={p.title} message={p.message} updated_at={p.updated_at}  username={p.username} />
           })}
        </div>
    )
}

export default Posts
